<html><head><title>Admin Dashboard - Bank Soal</title><style>body{font-family:-apple-system,BlinkMacSystemFont,"Segoe UI",sans-serif;background-color:#f8fafc;color:#334155;margin:0;padding:2rem}.container{max-width:1200px;margin:0 auto;background-color:white;padding:2rem;border-radius:12px;box-shadow:0 4px 6px #0000000d}header{display:flex;justify-content:space-between;align-items:center;border-bottom:1px solid #e2e8f0;padding-bottom:1rem;margin-bottom:2rem}h1{margin:0;color:#1e293b}.logout-link{display:inline-block;padding:.5rem 1rem;background-color:#ef4444;color:white;text-decoration:none;border-radius:8px;font-weight:500}table{width:100%;border-collapse:collapse}th,td{padding:1rem;text-align:left;border-bottom:1px solid #e2e8f0}th{background-color:#f1f5f9;text-transform:uppercase}.correct-answer{background-color:#dcfce7;color:#166534;font-weight:700}</style></head><body><div class="container"><header><h1>Bank Soal</h1><a href="<?=base_url('logout')?>" class="logout-link">Logout</a></header><table><thead><tr><th>ID</th><th>Level</th><th>Pertanyaan</th><th>Opsi A</th><th>Opsi B</th><th>Opsi C</th><th>Opsi D</th><th>Jawaban</th></tr></thead><tbody><?php if(!empty($questions)):foreach($questions as $q):?><tr><td><?=esc($q['id'])?></td><td><?=esc($q['level'])?></td><td><?=esc($q['question'])?></td><td class="<?=$q['correct']==='A'?'correct-answer':''?>"><?=esc($q['option_a'])?></td><td class="<?=$q['correct']==='B'?'correct-answer':''?>"><?=esc($q['option_b'])?></td><td class="<?=$q['correct']==='C'?'correct-answer':''?>"><?=esc($q['option_c'])?></td><td class="<?=$q['correct']==='D'?'correct-answer':''?>"><?=esc($q['option_d'])?></td><td><b><?=esc($q['correct'])?></b></td></tr><?php endforeach;else:?><tr><td colspan="8">Tidak ada soal ditemukan.</td></tr><?php endif;?></tbody></table></div></body></html>
